  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'currency';
  currjson: any = []
  base = 'USD'; 
  count2 = 'USD';
result:string ='1';

changebase(a:string)
{
  this.base = a;
  console.log(this.base)
}
